INSERT INTO `entities_drafts` (`id_entity_draft`, `id_entity`, `id_user`, `id_group`, `id_language`, `text_translated`, `edition`, `createdAt`, `updatedAt`) VALUES
(1, 1, 1, NULL, 3, 'Just making sure everything is good!\nNice!', NULL, '2017-09-06 11:36:02', '2017-09-06 11:36:08'),
(3, 29, 2, NULL, 1, 'jkkjkj  jhjhgjhgjg', NULL, '2017-09-11 11:07:11', '2017-09-11 11:07:22'),
(7, 41, 44, NULL, 2, 'Tu soignes ta virginité; et qu’y gagnes-tu? Ce n’est pas en allant chez Hadès que tu trouveras un amant, fillette.\nC’est chez les vivants que sont les délices de Cypris. Dans l’Achéron, c’est sous forme d’os et de poussière, vierge, que nous serons couchés.', 'Elsa', '2017-09-28 14:40:56', '2017-09-28 14:40:56'),
(8, 36, 5, NULL, 1, 'Test', '', '2017-09-29 14:19:03', '2017-09-29 14:19:03'),
(9, 20, 41, NULL, 1, '(Io) non mi considero più pazzo per i ragazzi, come una volta, \nma ora pazzo per le donne, e ora per me il disco (è) il crotalo,\ninvece della pelle naturale dei giovani mi piacciono le pelli di gesso\ne il colore artificiale del belletto rosso.\nL’Erimanto boscoso alimenterà delfini \ne l’onda bianca del mare (alimenterà) cerve veloci. ', '', '2017-12-14 09:15:11', '2017-12-14 09:42:23'),
(10, 20, 41, NULL, 1, 'Non più pazzo per i ragazzi, come un tempo,\nma per le donne; non più il disco, ma il crotalo mi affascina,\nné più la pelle naturale dei giovani, \nma la pelle incipriata e le labbra scarlatte.\nDelfini pascoleranno sull’Erimanto selvoso\ne cerve veloci tra le acque spumeggianti del mare. ', '', '2017-12-14 09:42:50', '2017-12-14 09:42:50');
